import React, { useState } from 'react';
import { useFormik } from 'formik';

import CurrentGoalList from './CurrentGoalList';
import AddGoalModal from './AddGoalModal'
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const Home = () => {
   const [show, setShow] = useState(false);

   const handleClose = () => setShow(false);
   const handleShow = () => setShow(true);

   const formik = useFormik({

      initialValues: {
        name: '',
        frequency: ''
      },

      onSubmit: values => {

        alert(JSON.stringify(values, null, 2));

      },
    });

   return (
      <div className="container d-flex flex-column align-items-center">
         <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
               <Modal.Title>Add a Goal</Modal.Title>
            </Modal.Header>

            <Modal.Body>
               <form onSubmit={formik.handleSubmit}>

                  <label htmlFor="text">Goal Name</label>
                  <input name="name" type="email" onChange={formik.handleChange} 
                         value={formik.values.name}
                  />

                  <Form.Group controlId="exampleForm.ControlSelect1">
                     <Form.Label>Select Frequency</Form.Label>
                     <Form.Control as="select">
                        <option>Once a Day</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                     </Form.Control>
                  </Form.Group>
                  <button type="submit">Submit</button>

               </form>
            </Modal.Body>

            <Modal.Footer>
               <Button variant="secondary" onClick={handleClose}>
                  Close
               </Button>
               <Button variant="primary" onClick={handleClose}>
                  Save Changes
               </Button>
            </Modal.Footer>
         </Modal>
         <div>
            <h1>
               Add a Goal
               <i className="fas fa-plus-square ml-3 add" onClick={handleShow}></i>
            </h1>
         </div>

         <h1 className="text-center">Current Goals</h1>
         <CurrentGoalList />
      </div> 
   );
};

export default Home;
